import React from 'react'
import { FaPlus, FaTrashCan } from "react-icons/fa6";


const Additem = () => {
  return (
    <div>
        <form className='addForm'>
            <input
                    type="text"
                    required
                    autoFocus
                    placeholder="Add Item"
                    value={initem}
                    onChange={(e) => e.target.value}
                />
                <button type="submit" onClick={() => {setItems(initem)}}>
                    <FaPlus />
                </button>
        </form>
    </div>
  )
}

export default Additem
